import React, { useState } from 'react';

const Checkout = ({ total }) => {
  const [discountCode, setDiscountCode] = useState('');
  const [finalTotal, setFinalTotal] = useState(total);

  const handleApplyDiscount = () => {
    if (discountCode === 'DISCOUNT10') {
      const discountAmount = total * 0.10; // ลด 10%
      setFinalTotal(total - discountAmount);
    } else {
      alert('คูปองไม่ถูกต้อง');
    }
  };

  return (
    <div className="mt-4">
      <h2 className="text-xl font-bold">Checkout</h2>
      <p>รวมราคา: ฿{total.toFixed(2)}</p>
      <p>ราคาหลังส่วนลด: ฿{finalTotal.toFixed(2)}</p>

      <div className="mt-2">
        <input
          type="text"
          value={discountCode}
          onChange={(e) => setDiscountCode(e.target.value)}
          placeholder="กรอกคูปองส่วนลด"
          className="form-control w-50"
        />
        <button className="btn btn-primary mt-2" onClick={handleApplyDiscount}>
          ใช้คูปอง
        </button>
      </div>

      <p>ค่าขนส่ง: ฿100</p>
      <p>รวมทั้งหมด: ฿{(finalTotal + 100).toFixed(2)}</p>
    </div>
  );
};

export default Checkout;
