import React, { useState } from 'react';

const ProductList = ({ products, onAddToCart }) => {
  const [selectedColors, setSelectedColors] = useState({});

  const handleColorChange = (e, product) => {
    setSelectedColors({ ...selectedColors, [product.id]: e.target.value });
  };

  return (
    <div className="row">
      {products.map((product) => (
        <div key={product.id} className="col-md-4 mb-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">{product.name}</h5>
              <p className="card-text">{product.description}</p>
              <p className="card-text">Price: à¸¿{product.price}</p>
              
              {/* Dropdown for selecting color */}
              <label htmlFor={`colorSelect-${product.id}`}>Select Color:</label>
              <select
                id={`colorSelect-${product.id}`}
                className="form-control mb-2"
                value={selectedColors[product.id] || ''}
                onChange={(e) => handleColorChange(e, product)}
              >
                <option value="" disabled>Select a color</option>
                {product.colors.map((color) => (
                  <option key={color} value={color}>{color}</option>
                ))}
              </select>

              <button
                className="btn btn-primary"
                onClick={() => onAddToCart(product, selectedColors[product.id])}
                disabled={!selectedColors[product.id]} // Disable if no color is selected
              >
                Add to Cart
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
