import React from 'react';

const ShoppingCart = ({ cartItems, onRemoveFromCart, onUpdateQuantity }) => {
  const handleIncreaseQuantity = (item) => {
    onUpdateQuantity(item.id, item.quantity + 1); // เพิ่มจำนวน
  };

  const handleDecreaseQuantity = (item) => {
    if (item.quantity > 1) {
      onUpdateQuantity(item.id, item.quantity - 1); // ลดจำนวนเมื่อมากกว่า 1
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="text-primary">Shopping Cart</h2>
      {cartItems.length === 0 ? (
        <p className="text-muted">Your cart is empty</p>
      ) : (
        <ul className="list-group">
          {cartItems.map((item) => (
            <li key={item.id} className="list-group-item d-flex justify-content-between align-items-center">
              <div>
                <h5 className="text-primary">{item.name}</h5>
                <p>Price: ฿{item.price}</p>
                <div className="d-flex align-items-center">
                  <p className="mb-0">Quantity: {item.quantity}</p>
                  <button 
                    className="btn btn-outline-primary btn-sm ml-2"
                    onClick={() => handleIncreaseQuantity(item)}
                  >
                    +
                  </button>
                  <button 
                    className="btn btn-outline-primary btn-sm ml-2"
                    onClick={() => handleDecreaseQuantity(item)}
                  >
                    -
                  </button>
                </div>
              </div>
              <div>
                <button 
                  className="btn btn-danger"
                  onClick={() => onRemoveFromCart(item)}
                >
                  Remove
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ShoppingCart;
