// App.js
import React, { useState } from 'react';
import ProductList from './ProductList';
import ShoppingCart from './ShoppingCart';
import Checkout from './Checkout'; // นำเข้า Checkout
import './App.css';

const App = () => {
  const [products] = useState([
    { 
      id: 1, 
      name: 'SteelSeries Arctis Nova Pro Wireless', 
      price: 100, 
      description: (
        <ul>
          <li>Driver: 40mm</li>
          <li>Frequency response (wired): 10–40,000 Hz</li>
          <li>Frequency response (wireless): 10–22,000 Hz</li>
          <li>Sensitivity: 93 dBSPL</li>
          <li>Impedance: 38 Ohm</li>
          <li>Active Noise Cancellation: 4-mic hybrid design with Transparency Mode</li>
          <li>Microphone Type: ClearCast Gen 2 - Fully Retractable Boom</li>
          <li>Microphone Polar Pattern: Bidirectional Noise-Canceling</li>
          <li>Microphone Frequency Response: 100-6500 Hz</li>
          <li>Microphone Sensitivity: -38 dBV/Pa</li>
          <li>Microphone Impedance: 2200 Ohm</li>
          <li>Battery Life: Up to 44 hours</li>
          <li>Battery: 2 batteries provided</li>
          <li>Bluetooth: 5.0</li>
          <li>Wireless: Low Latency 2.4 GHz</li>
          <li>Wireless Range: 12 meters</li>
        </ul>
      ),
    },
    { id: 2, name: 'HyperX Cloud Alpha', price: 200, description: 'Description 2' },
    { id: 3, name: 'Beyerdynamic MMX150', price: 300, description: 'Description 3' },
    { id: 4, name: 'Logitech G Pro X 2 Lightspeed Wireless', price: 300, description: 'Description 4' },
    { id: 5, name: 'H3PRO Hybrid Wireless', price: 300, description: 'Description 5' },
    { id: 6, name: 'Sony INZONE Buds', price: 300, description: 'Description 6' },
    { id: 7, name: 'Asus ROG Cetra', price: 300, description: 'Description 7' },
    { id: 8, name: 'Razer Hammerhead Pro HyperSpeed', price: 300, description: 'Description 8' },
    { id: 9, name: 'JBL Quantum TWS	', price: 300, description: 'Description 9' },
    { id: 10, name: 'Cougar Attila', price: 300, description: 'Description 10' },
  ]);

  const [cartItems, setCartItems] = useState([]);

  const handleAddToCart = (product) => {
    const existItem = cartItems.find((item) => item.id === product.id);
    if (existItem) {
      setCartItems(cartItems.map((item) =>
        item.id === product.id ? { ...existItem, quantity: existItem.quantity + 1 } : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
  };

  const handleRemoveFromCart = (product) => {
    setCartItems(cartItems.filter((item) => item.id !== product.id));
  };

  const handleUpdateQuantity = (product, newQuantity) => {
    if (newQuantity <= 0) {
      handleRemoveFromCart(product);
    } else {
      setCartItems(
        cartItems.map((item) =>
          item.id === product.id ? { ...item, quantity: newQuantity } : item
        )
      );
    }
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-2xl font-bold mb-4">Shopping Cart App</h1>

      {/* ส่วนแสดงรายการสินค้า */}
      <ProductList products={products} onAddToCart={handleAddToCart} />

      {/* ส่วนแสดงตะกร้าสินค้า */}
      <ShoppingCart cartItems={cartItems} onRemoveFromCart={handleRemoveFromCart} onUpdateQuantity={handleUpdateQuantity} />

      {/* ส่วนแสดงผลรวมราคาและคูปอง */}
      <Checkout total={cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0)} />
    </div>
  );
};

export default App;
