// ShoppingCart.js
import React from 'react';

const ShoppingCart = ({ cartItems, onRemoveFromCart, onUpdateQuantity }) => {
  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">Shopping Cart</h2>
      {cartItems.map((item) => (
        <div key={item.id} className="flex justify-between items-center mb-2">
          <div>
            <h3>{item.name}</h3>
            <p>฿{item.price} x {item.quantity}</p>
          </div>
          <div>
            <button onClick={() => onUpdateQuantity(item, item.quantity + 1)}>+</button>
            <button onClick={() => onUpdateQuantity(item, item.quantity - 1)}>-</button>
            <button onClick={() => onRemoveFromCart(item)} className="ml-4 bg-red-500 text-white py-1 px-2 rounded">Remove</button>
          </div>
        </div>
      ))}
      <div className="font-bold">Total: ฿{total}</div>
    </div>
  );
};

export default ShoppingCart;
