// ProductItem.js
import React from 'react';

const ProductItem = ({ product, onAddToCart }) => {
  return (
    <div className="bg-cyan-200 p-6 rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300">
      <h2 className="text-xl font-bold text-cyan-900 mb-2">{product.name}</h2>
      <p className="text-cyan-800 mb-4">{product.description}</p>
      <p className="text-lg font-semibold text-cyan-900 mb-4">à¸¿{product.price}</p>
      <button 
        className="bg-cyan-500 text-white py-2 px-4 rounded hover:bg-cyan-600 transition duration-300"
        onClick={() => onAddToCart(product)}
      >
        Add to Cart
      </button>
    </div>
  );
};

export default ProductItem;
